function saveArticle(e){e.preventDefault(),this.classList.toggle("saved"),this.classList.contains("saved")?this.getElementsByTagName("span")[0].innerHTML="Bookmarked":this.getElementsByTagName("span")[0].innerHTML="Bookmark"}function likeArticle(e){e.preventDefault(),this.classList.toggle("liked"),this.classList.contains("liked")?this.getElementsByTagName("span")[0].innerHTML="Gewaardeerd":this.getElementsByTagName("span")[0].innerHTML="Waardeer"}document.getElementById("saveButton").addEventListener("click",saveArticle),document.getElementById("likeButton").addEventListener("click",likeArticle);